document.querySelectorAll(".tv").forEach((e=>{e.insertAdjacentHTML("beforeend",'<div class="container">\n  <div class="monitor">\n    <div class="monitorscreen">\n       <img src="./original.gif" alt="img....." style="width:100%; ">\n    </div>\n  </div>\n</div>')})),(e=>{const t=document.querySelectorAll(".content"),n=[{id:0,title:"What is netflix ?",body:" Netflix is a streaming service that offers a wide variety of\n            award-winning TV shows, movies, anime, documentaries, and more on\n            thousands of internet-connected devices. You can watch as much as\n            you want, whenever you want without a single commercial – all for\n            one low monthly price. There's always something new to discover and\n            new TV shows and movies are added every week!"},{id:1,title:"How much does Netflix cost?",body:" Watch Netflix on your smartphone, tablet, Smart TV, laptop, or streaming device, all for one fixed monthly fee. Plans range from EUR7.99 to EUR13.99 a month. No extra costs, no contracts."},{id:2,title:"Where can I watch?",body:"Watch anywhere, anytime, on an unlimited number of devices. Sign in with your Netflix account to watch instantly on the web at netflix.com from your personal computer or on any internet-connected device that offers the Netflix app, including smart TVs, smartphones, tablets, streaming media players and game consoles.\n\nYou can also download your favorite shows with the iOS, Android, or Windows 10 app. Use downloads to watch while you're on the go and without an internet connection. Take Netflix with you anywhere."},{id:3,title:"How do I cancel?",body:"Netflix is flexible. There are no pesky contracts and no commitments. You can easily cancel your account online in two clicks. There are no cancellation fees – start or stop your account anytime."},{id:4,title:"What can I watch on Netflix?",body:"Netflix is flexible. There are no pesky contracts and no commitments. You can easily cancel your account online in two clicks. There are no cancellation fees – start or stop your account anytime."},{id:5,title:"How does the free trial work?",body:"Netflix is flexible. There are no pesky contracts and no commitments. You can easily cancel your account online in two clicks. There are no cancellation fees – start or stop your account anytime."}].map((({id:e,title:t,body:n})=>({title:`<div\n            type="button"\n            class="py-4 thumb d-flex justify-content-between border border-light"\n            data-bs-toggle="collapse"\n            data-bs-target="#collapse${e}"\n          >\n            <h3 class="ms-3 my-auto">${t}</h3>\n            <i class="fas fa-plus me-3 my-auto"></i>\n          </div>`,body:`<div class="collapse questions p-2" id="collapse${e}">\n            ${n}\n          </div>`})));for(data of n){let e=data.title,n=data.body;t.forEach((t=>{t.insertAdjacentHTML("beforeend",e),t.insertAdjacentHTML("beforeend",n)}))}})();